            .calculations(GetQuerySpecificationCalculationArgs.builder()
                .op("P99")
                .column("duration_ms")
                .build())
            .filters(GetQuerySpecificationFilterArgs.builder()
                .column("trace.parent_id")
                .op("does-not-exist")
                .build())
            .breakdowns("app.tenant")
